import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { getVideoGame, getVideoGameName } from '../../redux/actions/index.js';
import "./Buscador.css";

export default function Buscador() {
    const[input, setInput] = useState('');
    const dispatch = useDispatch();
    const inputHandler = (e) => {
        setInput(e.target.value)
    };
    const onClickHandler = () => {
        dispatch(getVideoGameName(input));
        console.log(input)
    };
    const HomeHandler = () => {
        dispatch(getVideoGame());
    };
    return (
        <div className="container">
            <input className="inputtext"
            type="text"
            placeholder="Busca tu Juego"
            name="input"
            onChange={(e) => inputHandler(e)}
            />
            <div>
                <button onClick={() => onClickHandler()} className="btnSearch">
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M19.023 16.977a35.13 35.13 0 0 1-1.367-1.384c-.372-.378-.596-.653-.596-.653l-2.8-1.337A6.962 6.962 0 0 0 16 9c0-3.859-3.14-7-7-7S2 5.141 2 9s3.14 7 7 7c1.763 0 3.37-.66 4.603-1.739l1.337 2.8s.275.224.653.596c.387.363.896.854 1.384 1.367l1.358 1.392.604.646 2.121-2.121-.646-.604c-.379-.372-.885-.866-1.391-1.36zM9 14c-2.757 0-5-2.243-5-5s2.243-5 5-5 5 2.243 5 5-2.243 5-5 5z"/></svg>
                    </button>
                <button onClick={() => HomeHandler()} className="btnSearch">Reestablecer</button>
            </div>
        </div>
    )
}
